pragma solidity 0.8.0;

import {
    SafeERC20,
    IERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IPokeMe {
    function gelato() external view returns (address payable);
}

abstract contract PokeMeReady {
    address public immutable pokeMe;
    address payable public immutable gelato;
    address public constant ETH = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    constructor(address _pokeMe) {
        pokeMe = _pokeMe;
        gelato = IPokeMe(_pokeMe).gelato();
    }

    modifier onlyPokeMe() {
        require(msg.sender == pokeMe, "PokeMeReady: onlyPokeMe");
        _;
    }

    function _transfer(uint256 _amount, address _paymentToken) internal {
        if (_paymentToken == ETH) {
            (bool success, ) = gelato.call{value: _amount}("");
            require(success, "_transfer: ETH transfer failed");
        } else {
            SafeERC20.safeTransfer(IERC20(_paymentToken), gelato, _amount);
        }
    }
}","keccak256":"0x4675c2ea835bb7c594a56d78c591b1603a04c13602ae8fe19732842b370ee33e","license":"UNLICENSED"}},"version":1}
